package com.udemy.dockerdemo;import lombok.RequiredArgsConstructor;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;@RestController@RequestMapping("/api/v1/")@RequiredArgsConstructorpublic class DemoController {    private final StudentRepository studentRepository;    @GetMapping("test")    public ResponseEntity<String> test() {        return ResponseEntity.ok("Hello World!");    }    @GetMapping("demo")    public ResponseEntity<List<Student>> demo() {        return ResponseEntity.ok(studentRepository.findAll());    }}